import React, { Component } from 'react';
import { connect } from 'react-redux';
import Chart from '../components/chart';

class WeatherList extends Component {
  renderWeather(cityData){
    console.log(cityData)
    const name = cityData.city.name;
    const country = cityData.city.country;
    const temps = cityData.list.map(weather => weather.main.temp)
    const presure = cityData.list.map(weather => weather.main.pressure)
    const humidty = cityData.list.map(weather => weather.main.humidity)
    return(
      <tr key = {name}>
      <th>{name},{country}</th>
      <th><Chart data={temps} color='orange'/></th>
      <th><Chart data={presure} color='green'/></th>
      <th><Chart data={humidty} color='blue'/></th>
    </tr>

    )
      }
        

  render() {
    return (
      <table>
        <thead>
          <tr>
            <th>city</th>
            <th>temps</th>
            <th>presure</th>
            <th>humidty</th>
          </tr>
        </thead>
        <tbody>
          {this.props.weather.map(this.renderWeather)}
        </tbody>
      </table>
    );
  }
}

// mapSrtateToProps funciton
function mapSrtateToProps(state){
  return{ weather: state.weather};
}

// connect mapping
export default connect(mapSrtateToProps)(WeatherList);
