import React, { Component } from 'react';
import { connect } from 'react-redux';
import Chart from '../components/chart';

class WeatherList extends Component {

  renderWeather (cityData) {
    
    const name = cityData.city.name
    const country = cityData.city.country
    const temps = cityData.list.map(weather => weather.main.temp);
    const pressures = cityData.list.map(weather => weather.main.pressure);
    const humidities = cityData.list.map(weather => weather.main.humidity);

    return (

          <tr key={name}>
            <th>{name}, {country}</th>
            <th><Chart data={temps} color='orange'/></th>
            <th><Chart data={pressures} color='green'/></th>
            <th><Chart data={humidities} color='black'/></th>
          </tr>
    );
    
  }
  render() {
    return (
      <table>
        <thead>
          <th>City</th>
          <th>Temperature</th>
          <th>pressure</th>
          <th>Humidity</th>
        </thead>
        <tbody>
        {this.props.weather.map(this.renderWeather)}
        </tbody>
      </table>
    )
  }

}

// mapSrtateToProps funciton
function mapStateToProps(state) {
  return {weather: state.weather}
}

// connect mapping
export default connect (mapSrtateToProps)(WeatherList);
