import {FETCH_POSTS, FETCH_POST, CREATE_POST, DELETE_POST} from "../actions";

export default function(state={}, action) {

    switch(action.type) {
        case FETCH_POSTS:
            console.log(action.payload.data)
            // return [{type: action.type, payload: action.payload}, ...state]

        default:
            return state;
    }
}
